example.snippet:
[Fact]
public async Task GetByIdAsync_Should_ReturnEntity_WhenExists()
{
    // Arrange
    var id = Guid.NewGuid();
    var entity = new SystemLogEntity(id) { Message = "Test" };
    _repository.GetAsync(id).Returns(entity);

    // Act
    var result = await _domainService.GetByIdAsync(id);

    // Assert
    result.Should().NotBeNull();
    result.Id.Should().Be(id);
    result.Message.Should().Be("Test");
}
